mapper.select:
SELECT 

FROM tbl_borrow_archives a

WHERE a.id = #{id}

SELECT
FROM tbl_borrow_archives a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.borrow_date >= #{startBorrowDate}
AND a.borrow_date <= #{endBorrowDate}
AND a.consult_unit LIKE '%'||#{consultUnit}||'%' '%'+#{consultUnit}+'%' concat('%',#{consultUnit},'%')
AND a.operator LIKE '%'||#{operator}||'%' '%'+#{operator}+'%' concat('%',#{operator},'%')
AND a.id in (select main_id from tbl_borrow_target where main_id = a.id and del_flag= '0' and name like concat('%', #{tarStr}, '%'))
AND a.id in (select main_id from tbl_borrow_person where main_id = a.id and del_flag = '0' and name like concat('%', #{perStr}, '%'))
AND b.return_time >= #{startBackDate}
AND b.return_time <= #{endBackDate}
AND b.return_person LIKE '%'||#{backOperator}||'%' '%'+#{backOperator}+'%' concat('%',#{backOperator},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM tbl_borrow_archives a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tbl_borrow_archives SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
