mapper.select:
SELECT (select label from sys_dict where id = a.unit and DEL_FLAG = '0') as unitName,
FROM tbl_borrow_person a
WHERE a.id = #{id}

SELECT (select label from sys_dict where id = a.unit and DEL_FLAG = '0') as unitName,
FROM tbl_borrow_person a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.main_id = #{mainId}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date ASC

SELECT 
	(select label from sys_dict where id = a.unit and DEL_FLAG = '0') as unitName,
	
FROM tbl_borrow_person a


	a.del_flag = #{DEL_FLAG_NORMAL}
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.create_date ASC

mapper.update:
UPDATE tbl_borrow_person SET 
	del_flag = #{DEL_FLAG_DELETE}

	
		WHERE id = #{id}
	
	
		WHERE main_id = #{mainId}
