mapper.select:
SELECT (select label from sys_dict where id = a.consult_unit and DEL_FLAG = '0') as consultUnitName,
FROM tbl_consult_archives a
WHERE a.id = #{id}

SELECT 
	(select label from sys_dict where id = a.consult_unit and DEL_FLAG = '0') as consultUnitName,

FROM tbl_consult_archives a


	a.del_flag = #{DEL_FLAG_NORMAL}


		AND a.borrow_date >= #{startBorrowDate}




		AND a.borrow_date <= #{endBorrowDate}



		AND a.consult_unit like concat('%', #{consultUnit}, '%')


		AND a.id in (select main_id from tbl_checked_target where main_id = a.id 
						and del_flag = '0' and name like concat('%', #{tarStr}, '%'))


		AND a.id in (select main_id from tbl_check_person where main_id = a.id 
						and del_flag = '0' and name like concat('%', #{perStr}, '%'))


		AND a.operator = #{operator}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.status, a.update_date DESC

SELECT 
	(select label from sys_dict where id = a.consult_unit and DEL_FLAG = '0') as consultUnitName,

FROM tbl_consult_archives a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tbl_consult_archives SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 
	(select label from sys_dict where id = a.consult_unit and DEL_FLAG = '0') as consultUnitName,

FROM tbl_consult_archives a


	a.del_flag = #{DEL_FLAG_NORMAL}


		AND a.borrow_date >= #{startBorrowDate}




		AND a.borrow_date <= #{endBorrowDate}



		AND a.consult_unit like concat('%', #{consultUnit}, '%')


		AND a.id in (select main_id from tbl_checked_target where main_id = a.id 
						and del_flag = '0' and name like concat('%', #{tarStr}, '%'))


		AND a.id in (select main_id from tbl_check_person where main_id = a.id 
						and del_flag = '0' and name like concat('%', #{perStr}, '%'))


		AND a.operator = #{operator}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date

SELECT (select label from sys_dict where id = a.consult_unit and DEL_FLAG = '0') as "consultUnitName", a.borrow_date as "borrowDate", a.id as "mainId", t.name as "name", t.unit as "unit", (select label from sys_dict where id = t.unit and DEL_FLAG = '0') as "unitName", t.duty as "duty", t.political_status as "politicalStatus" FROM tbl_consult_archives a LEFT JOIN tbl_checked_target t on a.id = t.main_id and t.del_flag = '0'
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.borrow_date >= #{startBorrowDate}
AND a.borrow_date <= #{endBorrowDate}
AND a.consult_unit like concat('%', #{consultUnit}, '%')
AND a.id in (select main_id from tbl_checked_target where main_id = a.id and del_flag = '0' and name like concat('%', #{tarStr}, '%'))
AND a.id in (select main_id from tbl_check_person where main_id = a.id and del_flag = '0' and name like concat('%', #{perStr}, '%'))
AND a.operator = #{operator}
ORDER BY a.update_date
