mapper.select:
SELECT 

FROM tbl_roll_out_back a

WHERE a.id = #{id}

SELECT 

FROM tbl_roll_out_back a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tbl_roll_out_back a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tbl_roll_out_back SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
