mapper.select:
SELECT (select label from sys_dict where id = a.save_unit and DEL_FLAG = '0') as saveUnitName, (select label from sys_dict where id = a.before_unit and DEL_FLAG = '0') as beforeUnitName,
FROM tbl_roll_out a
WHERE a.id = #{id}

SELECT (select label from sys_dict where id = a.save_unit and DEL_FLAG = '0') as saveUnitName, (select label from sys_dict where id = a.before_unit and DEL_FLAG = '0') as beforeUnitName,
FROM tbl_roll_out a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.roll_out_time >= #{startRollOutTime}
AND a.roll_out_time <= #{endRollOutTime}
AND a.recipient LIKE '%'||#{recipient}||'%' '%'+#{recipient}+'%' concat('%',#{recipient},'%')
AND a.save_unit LIKE '%'||#{saveUnit}||'%' '%'+#{saveUnit}+'%' concat('%',#{saveUnit},'%')
AND (concat(a.character, a.number) like concat('%', replace(REPLACE(#{batchNum}, '字', ''), '号', ''), '%'))
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT (select label from sys_dict where id = a.save_unit and DEL_FLAG = '0') as saveUnitName, (select label from sys_dict where id = a.before_unit and DEL_FLAG = '0') as beforeUnitName,
FROM tbl_roll_out a
a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

mapper.update:
UPDATE tbl_roll_out SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
