mapper.select:
SELECT 

FROM tbl_hand_over_files a

WHERE a.id = #{id}

SELECT 

FROM tbl_hand_over_files a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.main_id = #{mainId}


		AND a.name like concat('%', #{name}, '%')


		AND a.files_names like concat('%', #{filesNames}, '%')




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM tbl_hand_over_files a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE tbl_hand_over_files SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE main_id = #{mainId}

mapper.select:
SELECT DISTINCT a.main_id AS "mainId", a.name AS "name", a.original_no AS "originalNo" FROM tbl_hand_over_files a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name like concat('%', #{name}, '%')

select DISTINCT main_id, name, original_no from tbl_hand_over_files where del_flag = '0' and main_id in (select f.id from tbl_scattered_files f where f.del_flag = '0'
AND f.hand_over_unit LIKE '%'||#{scattered.handOverUnit}||'%' '%'+#{scattered.handOverUnit}+'%' concat('%',#{scattered.handOverUnit},'%')
AND f.hand_over_date >= #{scattered.startHandOverDate}
AND f.hand_over_date <= #{scattered.endHandOverDate}
AND f.operator LIKE '%'||#{scattered.operator}||'%' '%'+#{scattered.operator}+'%' concat('%',#{scattered.operator},'%')
AND f.recipient LIKE '%'||#{scattered.recipient}||'%' '%'+#{scattered.recipient}+'%' concat('%',#{scattered.recipient},'%')
AND f.status = #{scattered.status}
)
