mapper.select:
SELECT (select label from sys_dict where id = a.hand_over_unit and DEL_FLAG = '0') as handOverUnitName,
FROM tbl_scattered_files a
WHERE a.id = #{id}

SELECT (select label from sys_dict where id = a.hand_over_unit and DEL_FLAG = '0') as handOverUnitName,
FROM tbl_scattered_files a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.hand_over_unit LIKE '%'||#{handOverUnit}||'%' '%'+#{handOverUnit}+'%' concat('%',#{handOverUnit},'%')
AND a.hand_over_date >= #{startHandOverDate}
AND a.hand_over_date <= #{endHandOverDate}
AND a.operator LIKE '%'||#{operator}||'%' '%'+#{operator}+'%' concat('%',#{operator},'%')
AND a.recipient LIKE '%'||#{recipient}||'%' '%'+#{recipient}+'%' concat('%',#{recipient},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 
	(select label from sys_dict where id = a.hand_over_unit and DEL_FLAG = '0') as handOverUnitName,

FROM tbl_scattered_files a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tbl_scattered_files SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT (select label from sys_dict where id = a.hand_over_unit and DEL_FLAG = '0') as handOverUnitName,
FROM tbl_scattered_files a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.hand_over_unit LIKE '%'||#{handOverUnit}||'%' '%'+#{handOverUnit}+'%' concat('%',#{handOverUnit},'%')
AND a.hand_over_date >= #{startHandOverDate}
AND a.hand_over_date <= #{endHandOverDate}
AND a.operator LIKE '%'||#{operator}||'%' '%'+#{operator}+'%' concat('%',#{operator},'%')
AND a.recipient LIKE '%'||#{recipient}||'%' '%'+#{recipient}+'%' concat('%',#{recipient},'%')
and a.id in
#{item.mainId}
ORDER BY ${page.orderBy}
ORDER BY a.update_date
